import React, { Component } from 'react';
import { ProductConsumer } from '../Context';
import { Link } from 'react-router-dom';
import { ButtonContainer } from './Button';

export default class Details extends Component {
  render() {
    return (
      <ProductConsumer>
        {value => {
          const {
            id,
            company,
            img,
            info,
            price,
            title,
            inCart
          } = value.detailProduct;
          return (
            <div className='container'>
              <div className='col-10 mx-auto col-md-6 my-3 text-center'>
                <img src={img} className='img-fluid' alt='Product' />
              </div>
              <div className='row'>
                <div className='col-10 mx-auto text-center text-slanted text-capitalized text-blue my-5'>
                  <h1>Model : {title}</h1>
                  <h4 className='text-title text-uppercase text-muted mt-3 mb-2'>
                    <span className='text-uppercase'>{company}</span>
                  </h4>
                  <h4 className='text-blue'>
                    <strong>
                      Price : <span>Rp.</span> {price}{' '}
                    </strong>
                  </h4>
                  <p className='text-capitalize font-weight-bold mt-3 mb-0'>
                    Some info abot product :{' '}
                  </p>
                  <p className='text-muted lead'>{info}</p>
                  <div>
                    <Link to='/'>
                      <ButtonContainer> Back to produk</ButtonContainer>
                    </Link>
                    <ButtonContainer
                      disabled={inChart ? true : false}
                    ></ButtonContainer>
                  </div>
                </div>
              </div>
            </div>
          );
        }}
      </ProductConsumer>
    );
  }
}
